Input = Input[0].split("\n")

PlaceholderMap = []
DoubledRows = []
#Expand heightwise
for Row in range(len(Input)):
  DoubleIt = True
  for x in range(len(Input[Row])):
    if Input[Row][x] == "#":
      DoubleIt = False
  if DoubleIt:
    DoubledRows.append(Row)

#Expand widthwise
DoubledColumns = []
for Column in range(len(Input[0])):
  DoubleIt = True
  for x in range(len(Input)):
    if Input[x][Column] == "#":
      DoubleIt = False
  if DoubleIt:
    DoubledColumns.append(Column)

Galaxies = []
for Row in range(len(Input)):
  for Column in range(len(Input[0])):
    if Input[Row][Column] == "#":
      Galaxies.append([Row, Column])
print(Galaxies)

print(DoubledRows)
print(DoubledColumns)

def FindDistance(Galaxy, SecondaryGalaxy):
  Row1 = Galaxy[0]
  Column1 = Galaxy[1]
  Row2 = SecondaryGalaxy[0]
  Column2 = SecondaryGalaxy[1]
  Total = abs(Row1 - Row2) + abs(Column1 - Column2)
  for DoubledRow in DoubledRows:
    if ((Row1 > DoubledRow) and (Row2 < DoubledRow)) or ((Row1 < DoubledRow) and (Row2 > DoubledRow)):
      Total += 1000000 - 1
  for DoubledColumn in DoubledColumns:
    if ((Column1 > DoubledColumn) and (Column2 < DoubledColumn)) or ((Column1 < DoubledColumn) and (Column2 > DoubledColumn)):
      Total += 1000000 - 1
  return(Total)

Total = 0
for GalaxyIndex in range(len(Galaxies)):
  MiniTotal = 0
  for SecondaryGalaxyIndex in range(GalaxyIndex, len(Galaxies)):
    PathLength = FindDistance(Galaxies[GalaxyIndex], Galaxies[SecondaryGalaxyIndex])
    Total += PathLength
    MiniTotal += PathLength
  print(Total, MiniTotal, PathLength)